import React, { useState, useEffect } from 'react';
import axios from 'axios';
export default function WeatherAPI(){
    const [notapi,api] = useState(null)
    const [weather,setWeather] = useState("")
    function handleClick(e){
        e.preventDefault();
        axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${weather}&appid=07afb695e6aff4daa2b961878f8e5316`)
        .then(res=>{console.log(res.data);
        api(res.data)})
        }
    function handleTempK(K){
        return ((K-273.15)*1.8)+32;

    }
    return (<div>
        <form onSubmit={handleClick}>
            <input type="text" value={weather} onChange={(e)=>setWeather(e.target.value)}></input>
            <input type="submit"></input>
        </form>
        {notapi && <><p>{notapi.weather[0].main}</p>
        <p>Feels Like: {handleTempK(notapi.main.feel_like).toFixed(0)}&#176;</p>
        <p>Humidity: {notapi.main.humidity}</p>
        <p>Pressure: {notapi.main.pressure}</p>
        <p>{handleTempK(notapi.main.temp).toFixed(0)}&#176;</p>
        <p>{handleTempK(notapi.main.temp_max).toFixed(0)}&#176;</p>
        <p>{handleTempK(notapi.main.temp_min).toFixed(0)}&#176;</p>
        <img src={notapi.weather[0].icon}></img></>}
        {/* <img src={notapi.sprites.front_shiny}></img>
        <h1>{notapi.abilities[0].ability.name}</h1><h1>{notapi.abilities[1].ability.name}</h1></>
        } */}
        </div>);
    }
